import{_ as t,c as s,o,ag as a}from"./chunks/framework.BDwTZuFy.js";const m=JSON.parse('{"title":"Commands Reference ðŸ’»","description":"","frontmatter":{},"headers":[],"relativePath":"tubs-statusplugin/latest/commands.md","filePath":"tubs-statusplugin/latest/commands.md"}'),l={name:"tubs-statusplugin/latest/commands.md"};function i(r,e,n,d,c,u){return o(),s("div",null,e[0]||(e[0]=[a('<h1 id="commands-reference-ðŸ’»" tabindex="-1">Commands Reference ðŸ’» <a class="header-anchor" href="#commands-reference-ðŸ’»" aria-label="Permalink to &quot;Commands Reference ðŸ’»&quot;">â€‹</a></h1><p>Tub&#39;s Status Plugin primarily uses two base commands: <code>/status</code> for player-focused status management and <code>/tsp</code> (Tub&#39;s Status Plugin) for general plugin administration and some admin-specific status actions.</p><div class="tip custom-block"><p class="custom-block-title">Permissions</p><p>Command access is governed by permissions. Players will only see relevant subcommands they have permission for when using help commands.</p></div><h2 id="status-command" tabindex="-1"><code>/status</code> Command <a class="header-anchor" href="#status-command" aria-label="Permalink to &quot;`/status` Command&quot;">â€‹</a></h2><ul><li><strong>Aliases:</strong> None by default.</li><li><strong>Primary Target:</strong> Players managing their own status, or admins managing specific player statuses.</li><li><strong>Note:</strong> This command can only be executed by a player.</li></ul><h3 id="subcommands-for-status" tabindex="-1">Subcommands for <code>/status</code>: <a class="header-anchor" href="#subcommands-for-status" aria-label="Permalink to &quot;Subcommands for `/status`:&quot;">â€‹</a></h3><ul><li><h4 id="status-message" tabindex="-1"><code>/status &lt;message&gt;</code> <a class="header-anchor" href="#status-message" aria-label="Permalink to &quot;`/status &lt;message&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Sets your own status to the provided <code>&lt;message&gt;</code>.</li><li><strong>Permission:</strong> <code>StatusPlugin.setStatus</code></li><li><strong>Usage Example:</strong> <code>/status &amp;aOnline and Happy!</code></li><li><strong>Notes:</strong><ul><li>Use <code>&amp;_</code> for spaces if needed (e.g., <code>/status Hello&amp;_World</code>).</li><li>Color/formatting codes (e.g., <code>&amp;a</code>, <code>&amp;l</code>) require corresponding <code>StatusPlugin.formatting.&lt;type&gt;</code> permissions.</li><li>Subject to <code>maxStatusLength</code> from <code>config.yml</code>.</li><li>If <code>groupMode: true</code> in <code>config.yml</code>, this command is disabled for non-admin players (they must use <code>/status &lt;groupname&gt;</code>). Admins with <code>StatusPlugin.admin.setStatus</code> can still use this to set their own custom status.</li></ul></li></ul></li><li><h4 id="status-groupname" tabindex="-1"><code>/status &lt;groupname&gt;</code> <a class="header-anchor" href="#status-groupname" aria-label="Permalink to &quot;`/status &lt;groupname&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Sets your status to one of the predefined groups. Only functional if <code>groupMode: true</code> is enabled in <code>config.yml</code>.</li><li><strong>Permission:</strong> <code>StatusPlugin.group.set</code> (and specific group permissions, see <a href="./permissions.html">Permissions Guide</a>).</li><li><strong>Usage Example:</strong> <code>/status vip_group</code></li></ul></li><li><h4 id="status-remove" tabindex="-1"><code>/status remove</code> <a class="header-anchor" href="#status-remove" aria-label="Permalink to &quot;`/status remove`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Removes your own status.</li><li><strong>Permission:</strong> <code>StatusPlugin.setStatus</code></li><li><strong>Usage:</strong> <code>/status remove</code></li><li><strong>Note:</strong> If <code>groupMode: true</code>, this command might not be the primary way to clear a group status (typically you&#39;d switch to another group or an admin would remove it).</li></ul></li><li><h4 id="status-player-status-groupname" tabindex="-1"><code>/status &lt;player&gt; &lt;status|groupname&gt;</code> <a class="header-anchor" href="#status-player-status-groupname" aria-label="Permalink to &quot;`/status &lt;player&gt; &lt;status|groupname&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> (Admin) Sets the status of another <code>&lt;player&gt;</code> to the given <code>&lt;status&gt;</code> message or <code>&lt;groupname&gt;</code>.</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.setStatus</code></li><li><strong>Usage Examples:</strong><ul><li><code>/status Notch Server_Admin</code> (sets custom status)</li><li><code>/status Herobrine admin_group</code> (sets group status, if <code>admin_group</code> is defined)</li></ul></li><li><strong>Notes:</strong><ul><li>This command allows admins to set custom statuses for players even if <code>groupMode</code> is on.</li><li>If a group name is provided, it will attempt to set that group for the player.</li></ul></li></ul></li><li><h4 id="status-remove-player" tabindex="-1"><code>/status remove &lt;player&gt;</code> <a class="header-anchor" href="#status-remove-player" aria-label="Permalink to &quot;`/status remove &lt;player&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> (Admin) Removes the status of the specified <code>&lt;player&gt;</code>.</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.setStatus</code></li><li><strong>Usage Example:</strong> <code>/status remove Herobrine</code></li></ul></li></ul><h2 id="tsp-command-tub-s-status-plugin" tabindex="-1"><code>/tsp</code> Command (Tub&#39;s Status Plugin) <a class="header-anchor" href="#tsp-command-tub-s-status-plugin" aria-label="Permalink to &quot;`/tsp` Command (Tub&#39;s Status Plugin)&quot;">â€‹</a></h2><ul><li><strong>Aliases:</strong> None by default.</li><li><strong>Primary Target:</strong> Plugin administration, global settings, and console-accessible commands.</li><li><strong>Note:</strong> Can be executed by players with permission and by the console (unless specified).</li></ul><h3 id="subcommands-for-tsp" tabindex="-1">Subcommands for <code>/tsp</code>: <a class="header-anchor" href="#subcommands-for-tsp" aria-label="Permalink to &quot;Subcommands for `/tsp`:&quot;">â€‹</a></h3><ul><li><h4 id="tsp-help-colorcodes" tabindex="-1"><code>/tsp help [colorcodes]</code> <a class="header-anchor" href="#tsp-help-colorcodes" aria-label="Permalink to &quot;`/tsp help [colorcodes]`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Displays help information. <ul><li><code>/tsp help</code>: Shows available <code>/tsp</code> commands for the user.</li><li><code>/tsp help colorcodes</code>: Shows a list of Minecraft color and formatting codes.</li></ul></li><li><strong>Permission:</strong> Implicitly accessible if any other <code>/tsp</code> subcommand is.</li><li><strong>Usage Examples:</strong><ul><li><code>/tsp help</code></li><li><code>/tsp help colorcodes</code></li></ul></li></ul></li><li><h4 id="tsp-info" tabindex="-1"><code>/tsp info</code> <a class="header-anchor" href="#tsp-info" aria-label="Permalink to &quot;`/tsp info`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Displays information about Tub&#39;s Status Plugin, including version, author, and support links. May also show update availability if <code>checkUpdate: true</code>.</li><li><strong>Permission:</strong> Implicitly accessible.</li><li><strong>Usage:</strong> <code>/tsp info</code></li></ul></li><li><h4 id="tsp-reload" tabindex="-1"><code>/tsp reload</code> <a class="header-anchor" href="#tsp-reload" aria-label="Permalink to &quot;`/tsp reload`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Reloads the plugin&#39;s <code>config.yml</code> and <code>statuses.yml</code> files.</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.reload</code></li><li><strong>Usage:</strong> <code>/tsp reload</code></li><li><strong>Notes:</strong> Can be executed from the console.</li></ul></li><li><h4 id="tsp-setmaxlength-length" tabindex="-1"><code>/tsp setmaxlength &lt;length&gt;</code> <a class="header-anchor" href="#tsp-setmaxlength-length" aria-label="Permalink to &quot;`/tsp setmaxlength &lt;length&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Sets the maximum allowed character length for statuses (ignoring color codes and placeholders).</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.setMaxlength</code></li><li><strong>Arguments:</strong><ul><li><code>&lt;length&gt;</code>: (Required) A positive integer.</li></ul></li><li><strong>Usage Example:</strong> <code>/tsp setmaxlength 20</code></li><li><strong>Notes:</strong> Can be executed from the console.</li></ul></li><li><h4 id="tsp-resetmaxlength" tabindex="-1"><code>/tsp resetmaxlength</code> <a class="header-anchor" href="#tsp-resetmaxlength" aria-label="Permalink to &quot;`/tsp resetmaxlength`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> Resets the maximum status length to its default value (as per <code>config.yml</code> default, typically 15).</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.resetMaxlength</code></li><li><strong>Usage:</strong> <code>/tsp resetmaxlength</code></li><li><strong>Notes:</strong> Can be executed from the console.</li></ul></li><li><h4 id="tsp-setstatus-player-status-groupname" tabindex="-1"><code>/tsp setstatus &lt;player&gt; &lt;status|groupname&gt;</code> <a class="header-anchor" href="#tsp-setstatus-player-status-groupname" aria-label="Permalink to &quot;`/tsp setstatus &lt;player&gt; &lt;status|groupname&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> (Admin) Sets the status of <code>&lt;player&gt;</code> to the given <code>&lt;status&gt;</code> message or <code>&lt;groupname&gt;</code>.</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.setStatus</code></li><li><strong>Usage Examples:</strong><ul><li><code>/tsp setstatus Notch Server_Admin</code></li><li><code>/tsp setstatus Herobrine admin_group</code></li></ul></li><li><strong>Notes:</strong><ul><li>This command allows admins to set custom statuses for players even if <code>groupMode</code> is on.</li><li>If a group name is provided, it will attempt to set that group for the player.</li><li>Can be executed from the console.</li></ul></li></ul></li><li><h4 id="tsp-remove-player" tabindex="-1"><code>/tsp remove &lt;player&gt;</code> <a class="header-anchor" href="#tsp-remove-player" aria-label="Permalink to &quot;`/tsp remove &lt;player&gt;`&quot;">â€‹</a></h4><ul><li><strong>Description:</strong> (Admin) Removes the status of the specified <code>&lt;player&gt;</code>.</li><li><strong>Permission:</strong> <code>StatusPlugin.admin.setStatus</code></li><li><strong>Usage Example:</strong> <code>/tsp remove Herobrine</code></li><li><strong>Notes:</strong> Can be executed from the console.</li></ul></li></ul>',11)]))}const p=t(l,[["render",i]]);export{m as __pageData,p as default};
