import{_ as e,c as s,o as a,ag as i}from"./chunks/framework.BDwTZuFy.js";const p=JSON.parse(`{"title":"Tub's Status Plugin - Introduction","description":"","frontmatter":{"title":"Tub's Status Plugin - Introduction"},"headers":[],"relativePath":"tubs-statusplugin/latest/index.md","filePath":"tubs-statusplugin/latest/index.md"}`),o={name:"tubs-statusplugin/latest/index.md"};function r(n,t,l,u,d,c){return a(),s("div",null,t[0]||(t[0]=[i('<h1 id="tub-s-status-plugin-ðŸ’¬" tabindex="-1">Tub&#39;s Status Plugin ðŸ’¬ <a class="header-anchor" href="#tub-s-status-plugin-ðŸ’¬" aria-label="Permalink to &quot;Tub&#39;s Status Plugin ðŸ’¬&quot;">â€‹</a></h1><p align="center"><a href="https://discord.pluginz.dev"><img src="https://i.imgur.com/JgDt1Fl.png" width="300" alt="Discord Invite"></a><br><i>Please join the Discord if you have questions!</i><br><i>Developed by TubYoub</i></p><p>Tub&#39;s Status Plugin is a Minecraft plugin for Spigot/Paper servers that empowers players to express themselves by setting custom statuses. These statuses are visibly displayed in the tab list and above their heads in-game, adding a personal touch to your server community.</p><div class="tip custom-block"><p class="custom-block-title">Personalize Your Presence!</p><p>Let players share their mood, current activity, or a fun message with everyone on the server.</p></div><h2 id="core-features" tabindex="-1">Core Features <a class="header-anchor" href="#core-features" aria-label="Permalink to &quot;Core Features&quot;">â€‹</a></h2><ul><li><strong>Custom Player Statuses:</strong> Players can set their own unique status messages using <code>/status &lt;message&gt;</code>.</li><li><strong>Formatting Support:</strong> Allows usage of Minecraft color codes (<code>&amp;0-&amp;f</code>) and formatting codes (<code>&amp;k-&amp;o</code>, <code>&amp;r</code>) in statuses, controlled by permissions.</li><li><strong>Tab List Display:</strong> Player statuses are integrated into the tab list (if enabled).</li><li><strong>Chat Formatter:</strong> Player statuses can be shown in chat messages (if enabled).</li><li><strong>Admin Controls (primarily via <code>/tsp</code> command):</strong><ul><li>Set or remove other players&#39; statuses.</li><li>Reload plugin configuration and statuses.</li><li>Set and reset maximum status length.</li><li>View plugin information and help.</li></ul></li><li><strong>Persistence:</strong> Statuses are saved and persist across server restarts and player relogs.</li><li><strong>PlaceholderAPI Integration:</strong><ul><li>Provides placeholders for use in other plugins (e.g., <code>%tubsstatusplugin_status%</code>).</li><li>Allows use of other PAPI placeholders within status messages.</li></ul></li><li><strong>LuckPerms Integration:</strong> Can display LuckPerms prefixes and suffixes within the status format using <code>%LP_prefix%</code> and <code>%LP_suffix%</code>.</li><li><strong>Group Mode:</strong> Optionally restrict players to choosing statuses from predefined groups, with fine-grained permission control for each group.</li><li><strong>Configurable:</strong> Control maximum status length, chat/tab list formatting, status wrapping characters, logging level, and more.</li><li><strong>Update Checker:</strong> Notifies admins of new plugin versions.</li></ul><p>Get started by following the <a href="./installation.html">Installation Guide</a>.</p>',7)]))}const m=e(o,[["render",r]]);export{p as __pageData,m as default};
