import{_ as t,c as r,o,ag as a}from"./chunks/framework.BDwTZuFy.js";const u=JSON.parse('{"title":"VelocityPteroPower ⚡️","description":"","frontmatter":{},"headers":[],"relativePath":"VelocityPteroPower/0-9-4/index.md","filePath":"VelocityPteroPower/0-9-4/index.md"}'),i={name:"VelocityPteroPower/0-9-4/index.md"};function n(s,e,l,c,p,d){return o(),r("div",null,e[0]||(e[0]=[a('<h1 id="velocitypteropower-⚡️" tabindex="-1">VelocityPteroPower ⚡️ <a class="header-anchor" href="#velocitypteropower-⚡️" aria-label="Permalink to &quot;VelocityPteroPower ⚡️&quot;">​</a></h1><p><img src="https://img.shields.io/badge/Velocity-green" alt="Static Badge"><a href="https://forthebadge.com" target="_blank" rel="noreferrer"><img src="https://forthebadge.com/images/badges/works-on-my-machine.svg" alt="forthebadge"></a></p><p><a href="https://www.buymeacoffee.com/tubyoub" target="_blank"><img src="https://cdn.buymeacoffee.com/buttons/default-orange.png" alt="Buy Me A Coffee" height="41" width="174"></a></p><p align="center"><a href="https://discord.pluginz.dev"><img src="https://i.imgur.com/JgDt1Fl.png" width="300" alt="Discord Invite"></a><br><i>Please join the Discord if you have questions!</i></p><p>Welcome to the official documentation for <strong>VelocityPteroPower (VPP)</strong>!</p><p>VPP is a powerful Velocity proxy plugin designed to seamlessly integrate your Minecraft servers managed by the <a href="https://pterodactyl.io/" target="_blank" rel="noreferrer">Pterodactyl</a> or <a href="https://pelican.dev/" target="_blank" rel="noreferrer">Pelican</a> Server Panel. It provides dynamic control over your servers, enabling automatic startup when players connect and intelligent shutdown of idle instances, optimizing your server resources and player experience.</p><p>This project is a port of the popular <a href="https://github.com/Kamesuta/BungeePteroPower" target="_blank" rel="noreferrer">BungeePteroPower</a> plugin, bringing its robust functionality to the Velocity proxy platform.</p><div class="tip custom-block"><p class="custom-block-title">What can VPP do for you?</p><p>VPP automates the tedious parts of managing a dynamic server network, such as starting servers on demand and stopping them when they&#39;re not in use. This saves server resources and provides a smoother, more responsive experience for your players.</p></div><h2 id="key-features-✨" tabindex="-1">Key Features ✨ <a class="header-anchor" href="#key-features-✨" aria-label="Permalink to &quot;Key Features ✨&quot;">​</a></h2><ul><li><strong>🚀 Dynamic Server Management:</strong> Automatically starts backend Minecraft servers when players attempt to connect to them.</li><li><strong>💤 Idle Server Shutdown:</strong> Intelligently configures servers to automatically shut down when they are empty for a specified period, conserving valuable server resources.</li><li><strong>🔧 Manual Control:</strong> Provides administrators with in-game commands to manually start, stop, and restart managed servers.</li><li><strong>🚦 Rate Limit Handling:</strong> Respects the panel API&#39;s rate limits to prevent issues and ensure stable communication with your Pterodactyl/Pelican panel.</li><li><strong>🏝️ Forced Host Redirection:</strong> Players connecting through forced hosts to an offline server can be temporarily redirected to a configured &quot;limbo&quot; server while their target server starts up.</li><li><strong>✅ Whitelist Integration:</strong> Optionally fetches and enforces server whitelists directly from the panel for managed servers.<div class="warning custom-block"><p class="custom-block-title">MC Server Soft Limitation</p><p>Please note that the whitelist fetching feature is <strong>not supported</strong> if your panel is MC Server Soft due to API differences.</p></div></li><li><strong>🔄 Config Reload:</strong> Allows for easy reloading of the plugin&#39;s configuration files without requiring a full proxy restart.</li><li><strong>🔔 Update Checks:</strong> Keeps you informed about new plugin versions with automatic update checks on startup.</li></ul><p>VelocityPteroPower aims to streamline the management of your dynamic Minecraft server infrastructure, offering a robust and user-friendly solution for both players and administrators.</p><hr><p><strong>Ready to dive in?</strong></p><ul><li>Learn how to install the plugin: <a href="./installation.html"><strong>Installation Guide →</strong></a></li><li>Understand all configuration options: <a href="./configuration.html"><strong>Configuration Details →</strong></a></li><li>Explore how VPP works: <a href="./core-mechanics.html"><strong>Core Mechanics →</strong></a></li></ul>',14)]))}const h=t(i,[["render",n]]);export{u as __pageData,h as default};
